import { useState} from 'react'
// import validator from 'validator';

const Register = (props) => {
  let formValid = false
    const [enteredName,setName] = useState('s')

    const [enteredEmail,setEmail] = useState('')

    console.log('hello')
    let emptyName = false;
    let invalidEmail = false;

    const nameChangeHandler =(event)=>
    {
        setName(event.target.value)
    }

    const emailChangeHandler =(event)=>
    {
        setEmail(event.target.value)
    }
    console.log(typeof(enteredName))
    
    if(enteredName.trim().length === 0)
    {
        emptyName = true;
        formValid= false;
    }
    if(enteredEmail.trim().length===0)
    {
        invalidEmail = true;
        formValid= false;
    }

    else{
        formValid = true
    }
  const submitHandler = (event) => {
    event.preventDefault()
    // console.log(enteredName)
  }

  const nameClasses = !emptyName ? 'formcontrol' : 'form-control invalid'
  const emailClasses = !invalidEmail ? 'formcontrol' : 'form-control invalid'

  return (
    <form onSubmit={submitHandler}>
      <div className={nameClasses}>
        <label htmlFor='name'>Name</label>
        <input type='text' id='name' onChange={nameChangeHandler} value={enteredName} />
        {emptyName && <p className='error-text'>Please Enter a Name!</p>}
      </div>

      <div className={emailClasses}>
        <label htmlFor='email'>Email</label>
        <input type='text' id='email' onChange={emailChangeHandler}  value={enteredEmail} />
        {invalidEmail && <p className='error-text'>Please Enter a valid Email!</p>}
      </div>


      <div className="form-actions">
        <button disabled={!formValid}>Submit</button>
      </div>
    </form>
  );
};

export default Register;
